    "bibtex": "@article{Clark.2010,\n    abstract = {Interns in a US teacher education program were surveyed regarding their attitudes towards diversity, particularly issues of sexuality, their feelings of ``cultural competence'' around diversity, and the repertoires of practices and resources they feel prepared to draw on in their work with middle and high schools students (11--18 year olds). Analysis of course materials provided insight into distinctions between anti-homophobia and LGBTQ-ally stances among students. Extensive semi-structured interviews with 7 students explored these issues in more depth, documenting their sense of preparedness and cultural competence, where locate and how they identify ``useful experiences in learning how to teach LGBTQ youth, and the concerns they face in their work against heterosexism and homophobia in schools.},\n    author = {Clark, Caroline T.},\n    year = {2010},\n    title = {Preparing LGBTQ-allies and combating homophobia in a U.S. teacher education program},\n    keyword = {Diversity;Heteronormativit{\\\"a}t // heteronormativity;Homofeindlichkeit // homophobia;Interkulturell // intercultural;Lehrer*innen // Teachers;Sexualit{\\\"a}t // sexuality;soziale Ungleichheit und/oder Gerechtigkeit // social inequality and/or justice},\n    pages = {704--713},\n    volume = {26},\n    number = {3},\n    issn = {0742051X},\n    journal = {Teaching and Teacher Education},\n    doi = {10.1016/j.tate.2009.10.006}\n}",
